import React from 'react';
import {
  Button,
  FormControl,
  FormLabel,
  Input,
  VStack,
  useMediaQuery,
} from '@chakra-ui/react';
import { useHistory } from 'react-router-dom';

const BookingDetails = () => {
  const [isSmallScreen] = useMediaQuery('(max-width: 400px)');
  const history = useHistory();

  const handleSubmit = () => {
    // Handle form submission and store booking details
    // You can use state management or API calls here

    // After handling submission, navigate to the payment page
    history.push('/payment');
  };

  return (
    <VStack spacing="4px">
      <FormControl id="name" isRequired mb="3">
        <FormLabel>Name</FormLabel>
        <Input placeholder="Enter Your Name" />
      </FormControl>
      {/* Add more form fields as needed (e.g., date, number of people, etc.) */}
      <Button
        bg="black"
        color="white"
        _hover={{
          boxShadow: 'none',
          transition: 'none',
        }}
        onClick={handleSubmit}
        fontSize={isSmallScreen ? '15px' : '18px'}
        width={isSmallScreen ? '85%' : '70%'}
      >
        Continue to Payment
      </Button>
    </VStack>
  );
};

export default BookingDetails;
