import React, { useState } from 'react';
import {
  Button,
  FormControl,
  FormLabel,
  Input,
  Select,
  VStack,
  useMediaQuery,
  AlertDialog,
  AlertDialogBody,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogContent,
  AlertDialogOverlay,
} from '@chakra-ui/react';
import { useHistory } from 'react-router-dom';

const UpiPayment = () => {
    const [isSmallScreen] = useMediaQuery('(max-width: 400px)');
    const history = useHistory();
  
    const [upiDetails, setUpiDetails] = useState({
      selectedUpiApp: '',
      upiId: '',
      amount: '',
    });
  
    const [isOpen, setIsOpen] = useState(false);
    const onClose = () => setIsOpen(false);
    const cancelRef = React.useRef();

    const handleInputChange = (e) => {
      const { name, value } = e.target;
      setUpiDetails({
        ...upiDetails,
        [name]: value,
      });
    };
  
    const handleProceedToPay = () => {
      // Validate UPI details before proceeding to pay
      if (!upiDetails.selectedUpiApp || !upiDetails.upiId) {
        alert('Please fill in all the required UPI details.');
        return;
      }
  
      // Handle payment processing logic here
      console.log('Proceeding to pay via UPI with details:', upiDetails);
  
      // For demonstration purposes, redirect to a success page
      setIsOpen(true);
      setTimeout(() => {
        history.push('/payment-success');
      }, 1000);
    };
  
    return (
      <VStack spacing="4px">
        <FormControl id="selectedUpiApp" isRequired mb="3">
          <FormLabel>Select UPI App</FormLabel>
          <Select
            placeholder="Select UPI App"
            name="selectedUpiApp"
            value={upiDetails.selectedUpiApp}
            onChange={handleInputChange}
          >
            <option value="google-pay">Google Pay</option>
            <option value="phonepe">PhonePe</option>
            <option value="paytm">Paytm</option>
            {/* Add more UPI apps as needed */}
          </Select>
        </FormControl>
  
        <FormControl id="upiId" isRequired mb="3">
          <FormLabel>Enter UPI ID</FormLabel>
          <Input
            placeholder="Enter UPI ID"
            name="upiId"
            value={upiDetails.upiId}
            onChange={handleInputChange}
          />
        </FormControl>

        <FormControl id="amount" isRequired mb="3">
        <FormLabel>Enter Amount</FormLabel>
        <Input
          placeholder="Enter Amount"
          type="number"
          name="amount"
          value={upiDetails.amount}
          onChange={handleInputChange}
        />
      </FormControl>
  
        <Button
          bg="black"
          color="white"
          _hover={{
            boxShadow: 'none',
            transition: 'none',
          }}
          onClick={handleProceedToPay}
          fontSize={isSmallScreen ? '15px' : '18px'}
          width={isSmallScreen ? '85%' : '70%'}
        >
          Proceed to Pay
        </Button>

        <AlertDialog
        isOpen={isOpen}
        leastDestructiveRef={cancelRef}
        onClose={onClose}
      >
        <AlertDialogOverlay>
          <AlertDialogContent>
            <AlertDialogHeader fontSize="lg" fontWeight="bold">
              Payment Successful
            </AlertDialogHeader>

            <AlertDialogBody>
              Thank you for your payment!
            </AlertDialogBody>

            <AlertDialogFooter>
              <Button ref={cancelRef} onClick={onClose}>
                Close
              </Button>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialogOverlay>
      </AlertDialog>

      </VStack>
    );
  };
  

export default UpiPayment;
